mapper.insert:
/* BoardServiceImpl.insertBoard */
    INSERT INTO board (
        board_title
        , board_contents
        , regist_id
        , regist_date
    ) VALUES (
        #{boardTitle}
        , #{boardContents}
        , #{registId}
        , NOW()
    )

mapper.update:
/* BoardServiceImpl.updateBoard */
    UPDATE
        board
    SET
        board_title = #{boardTitle}
        , board_contents = #{boardContents}
        , update_id = #{updateId}
        , update_date = NOW()
    WHERE
        board_id = #{boardId}
